import React, { useState } from 'react';
import { User } from '../types/User';

interface UserTableProps {
  users: User[]; // Список пользователей для отображения
}

const UserTable: React.FC<UserTableProps> = ({ users }) => {
  const [highlightedRowId, setHighlightedRowId] = useState<number | null>(null); // ID выделенной строки

  return (
    <table border={1} style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
      <thead>
        <tr>
          <th>ID</th>
          <th>Имя</th>
          <th>Никнейм</th>
          <th>Email</th>
          <th>Город</th>
          <th>Телефон</th>
          <th>Дата рождения</th>
          <th>Компания</th>
        </tr>
      </thead>
      <tbody>
        {users.map(user => (
          <tr
            key={user.id}
            onClick={() => setHighlightedRowId(user.id)} // Подсветка строки при клике
            style={{
              backgroundColor: highlightedRowId === user.id ? '#f0f8ff' : 'transparent',
              cursor: 'pointer',
            }}
          >
            <td>{user.id}</td>
            <td>{user.name}</td>
            <td>{user.username}</td>
            <td>{user.email}</td>
            <td>{user.address.city}</td>
            <td>{user.phone}</td>
            <td>{user.birthdate}</td>
            <td>{user.company.name}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default UserTable;
